import { isMobile } from 'react-device-detect';
import { Link, animateScroll as scroll } from "react-scroll";

import InstagramIcon from '@material-ui/icons/Instagram';
import FacebookIcon from '@material-ui/icons/Facebook';
import LinkedInIcon from '@material-ui/icons/LinkedIn';
import TwitterIcon from '@material-ui/icons/Twitter';
import Paper from '@material-ui/core/Paper';
// import Button from "components/CustomButtons/Button.js";

import saksham from 'assets/img/image/teams/team20/saksham.jpg';
import ayush from 'assets/img/image/teams/team20/Aayush.webp';
import nitesh from 'assets/img/image/teams/team20/Nitesh Thota.webp';
import Ali from 'assets/img/image/teams/team20/Rahamath Ali.webp';
import Fenil from 'assets/img/image/teams/team20/Fenil.jpg';
import hemangi from 'assets/img/image/teams/team20/Hemangi.jpeg';
import Lekha from 'assets/img/image/teams/team20/Lekha K.png';
import Aravind from 'assets/img/image/teams/team20/aravind.webp';
import Tuhin from 'assets/img/image/teams/team20/Tuhin.jpg';
import bgImage from 'assets/img/sign.jpg';
import bgImageMob from 'assets/img/Team.jpg';
import Arrowd from "@material-ui/icons/ArrowDropDown";
import { Button } from '@material-ui/core';


const useStyles = makeStyles({

  container: {
    zIndex: '10',
    display: 'flex',
    flexWrap: 'wrap',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'space-around'
  },
  title: {
    fontFamily: 'gotham',
    textStrokeColor: '#fff',
    textFillColor: 'transparent',
  },
  content: {
    display: 'flex',
  },
  box: {
    display: 'flex',
    flexDirection: 'column',
    backgroundColor: 'transparent',
    alignItems: 'center',
    justifyContent: 'space-around'
  },
  imag: {
    borderRadius: '50%',
    objectFit: 'cover',
    zIndex: '100'
  },
  Status: {
    color: '#f5f3f0',
    zIndex: '100'
  },
  iconTray: {
    width: '70%',
    display: 'flex',
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-around',
    zIndex: '100'
  },

  cover: {
    position: 'absolute',
    zIndex: '10'
  },

  downvote: {
    height: '5vh',
    width: '100%',
    zIndex: '10000',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: 'black',
    opacity: '0.4',
    transition: '250ms ease-in-out',
    '&:hover': {
      opacity: '0.7'
    }
  },
  arrow: {
    width: "100px",
    height: "100px",
    transition: '1s',
    color: '#fff',
    opacity: '0.3'
  },

  '@media (min-width: 800px)': {
    container: {
      height: '115vh',
    },
    title: {
      fontSize: '6vw',
      textStrokeWidth: '5px',
      marginTop: '2vh'
    },
    content: {
      flexWrap: 'wrap',
      width: '100%',
      alignItems: 'center',
      justifyContent: 'space-around'
    },
    box: {
      height: '37vh',
      width: '16vw',
    },
    break: {
      flexBasis: '100%',
      height: '5vh'
    },
    imag: {
      marginTop: '25px',
      height: '20vh',
      width: '10vw',
    },
    cover: {
      height: '34vh',
