import React from 'react';
import GoogleMapReact from "google-map-react";
import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles({
        container: {
            height: '40vh'
        }
});

const MyMarker = ({ text, tooltip }) => (
    <div className="circle">
      <span className="circleText" title={tooltip}>
        {text}
      </span>
    </div>
);

const points = [
    { id: 1, title: "Round Pond", lat: 51.506, lng: -0.184 },
    { id: 2, title: "The Long Water", lat: 51.508, lng: -0.175 },
    { id: 3, title: "The Serpentine", lat: 51.505, lng: -0.164 }
];

const Footer = (props) => {

    const classes = useStyles();

    return(
        <div className={classes.container}>            
            <GoogleMapReact
        bootstrapURLKeys={{
          key: "9d2pGO4LAbSUarGGX4G1XYn1Almz7GVG",
          language: "en",
          region: "US"
        }}
        defaultCenter={{ lat: 12.822126, lng: 80.038833 }}
        defaultZoom={15}
      >
        {points.map(({ lat, lng, id, title }) => {
          return (
            <MyMarker
              key={id}
              lat={lat}
              lng={lng}
              text={id}
              tooltip={title}
            />
          );
        })}
      </GoogleMapReact>
        </div>
    );
};

export default Footer;
