import React, { useState, useEffect, useRef } from 'react';

import { Link, animateScroll as scroll } from "react-scroll";
import { makeStyles } from "@material-ui/core/styles";
import 'assets/css/sponsor.css';
import Arrowd from "@material-ui/icons/ArrowDropDown";

// const useStyles = makeStyles({
//     // #00F8C8 #E0E1EE
//     container: {
//         position: 'relative',
//         height: '150vh',
//         backgroundColor: '#6C44D2',
//         // paddingTop: '5vh'
//     },
//     title: {
//         position: 'absolute',
//         fontSize: '10vh',
//         marginLeft: '7vw',
//         marginTop: '10vh',
//         color: '#00F8C8',
//     },

// });


function getWindowDimensions() {
    const { innerWidth: width, innerHeight: height } = window;
    return {
        width,
        height
    };
}

export default function SectionSponsorship(props) {
    // const classes = useStyles();

    const prevScrollY = useRef(0);
    const [windowDimensions, setWindowDimensions] = useState(getWindowDimensions());

    useEffect(() => {
        ////////////////////////////resize event///////////////////////////
        function handleResize() {
            setWindowDimensions(getWindowDimensions());
            console.log(windowDimensions.width);
        }
        window.addEventListener('resize', handleResize);

        ////////////////scroll event//////////////////////////
        const handleScroll = () => {
            const currentScrollY = window.scrollY;
            prevScrollY.current = currentScrollY;
            console.log(currentScrollY);
            let element = document.getElementById('sponsortitle');
            if (currentScrollY > 8914 && currentScrollY < 9263) {
                element.className = 'titleDuring';
            } else if (currentScrollY >= 9263) {
                element.className= 'titleAfter';
            }
            else {
                element.className = 'title';
            }
        };

        window.addEventListener("scroll", handleScroll, { passive: true });

        return () => window.removeEventListener("scroll", handleScroll);
        return () => window.removeEventListener('resize', handleResize);
    }, [prevScrollY]);


    return (
        <div className='container'>
            <div id="sponsortitle" className='title'>
                <h1 style={{fontSize: '15vh', fontWeight: '100px'}}>SPONSORS</h1>
            </div>
            <div className='wheeleez'>    
            </div>
        </div>
    );
}
