import React from "react";
import ReactPlayer from 'react-player';
import { makeStyles } from "@material-ui/core/styles";

import Box from '@material-ui/core/Box';

const useStyles = makeStyles({
    vidCard: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-around',
        flexWrap: 'wrap',
        marginTop: '10vh',
        height: '140vh',
        width: '95vw',
    },
    player: {
        height: '57vh',
        width: '50vw',
    },
    title: {
        fontFamily: "army",
        fontSize: "8em",
        fontWeight: "600",
        color: "#7900db",
        textShadow: '2px 2px 4px #240638'
    },
    title: {
        fontFamily: "army",
        fontSize: "7em",
        fontWeight: "600",
        color: "#7900db",
        // verticalAlign: 'top',
        textShadow: '2px 2px 4px #240638'
    },
    irc: {
        width: '35vw',
        height: '20vh',
    },

    '@media (max-width: 1030px)': {
        player: {
            height: '45vh',
            width: '50vw',
        },
    },

    '@media (max-width: 800px)': {
        vidCard: {
            marginBottom: '10vh',
            height: '90vh',
            width: '100vw',
        },
        mobplaya1: {
            position: 'absolute',
            marginTop: '10vh',
            width: '87vw',
            height: '30vh',
            // boxShadow: '5px 5px 7px 7px #888888'
        },
        mobplaya2: {
            position: 'absolute',
            marginTop: '60vh',
            width: '87vw',
            height: '30vh',
            // boxShadow: '5px 5px 7px 7px #888888'
        },
        title: {
            fontFamily: "army",
            fontSize: "10vw",
            fontWeight: "600",
            display: "inline-block",
            position: "absolute",
            marginLeft: "23vw",
            marginTop: "2vh",
            color: "#7900db"
        },
        title2: {
            fontFamily: "army",
            fontSize: "10vw",
            fontWeight: "600",
            display: "inline-block",
            position: "absolute",
            marginLeft: "13vw",
            marginTop: "45vh",
            width: "80vw",
            lineHeight: "5vh",
            color: "#7900db"
        },
        hori1: {
            position: 'absolute',
        },
        hori2: {
            position: 'absolute',

        },
        ver1: {
            position: 'absolute',
            // height: '13vh',
            // marginLeft: '80.5vw',
            // marginTop: '31vh',
            // borderLeft: '15px solid #4f00a8'
        },
        ver2: {
            position: 'absolute',

        },
        hori3: {
            position: 'absolute',

        },
        ver3: {
            position: 'absolute',

        },
        hori4: {
            position: 'absolute',

        },
        ver4: {
            position: 'absolute',

        },
    }
});

export default function SectionVideo() {
    const classes = useStyles();

    return (<div>
        <div className={classes.vidCard}>
            <Box boxShadow={24} className={classes.player}>
                <ReactPlayer url='https://youtu.be/2gt8fW8TD7c' controls='true' volume='1' muted='true' height='100%' width='100%' loop='true' playing />
            </Box>
            <div className={classes.title}>
                SAR-2020
            </div>
            {/* <div></div> */}
            <div className={classes.irc}>
                <h1 className={classes.title}>IRC-2020-After Movie</h1>
            </div>
            <Box boxShadow={24} className={classes.player}>
                <ReactPlayer url='https://youtu.be/B3iRm-0kSSY' controls='true' volume='1' muted='true' height='100%' width='100%' loop='true' playing />
            </Box>
        </div>
    </div>
    );
}
