import React from "react";
import ReactPlayer from 'react-player';
import { makeStyles } from "@material-ui/core/styles";

import Box from '@material-ui/core/Box';

const useStyles = makeStyles({
    vidCard: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-around',
        flexWrap: 'wrap',
        marginTop: '10vh',
        height: '140vh',
        width: '95vw',
    },
    player: {
        height: '57vh',
        width: '50vw',
    },
    title: {
        fontFamily: "army",
        fontSize: "7em",
        fontWeight: "600",
        color: "#7900db",
        textShadow: '2px 2px 4px #240638'
    },
    irc: {
        width: '35vw',
        height: '20vh',
    },

    '@media (max-width: 1030px)': {
        vidCard:{
            height: '60vh',
            marginBottom: '10vh'
        },
        player: {
            height: '23.5vh',
            width: '55vw',
        },
        title:{
            fontSize: '4em',
            textShadow: '2px 2px 6px #240638'
        }
    },

    '@media (max-width: 800px)': {
        vidCard:{
            height: '90vh',
            marginBottom: '10vh'
        },
        player: {
            height: '35vh',
            width: '90vw',
        },
    }
});

export default function SectionVideo() {
    const classes = useStyles();

    return (<div>
        <div className={classes.vidCard}>
            <Box boxShadow={24} className={classes.player}>
                <ReactPlayer url='https://youtu.be/2gt8fW8TD7c' controls='true' volume='1' muted='true' height='100%' width='100%' loop='true' playing />
            </Box>
            <div className={classes.title}>
                SAR-2020
            </div>
            {/* <div></div> */}
            <div className={classes.irc}>
                <h1 className={classes.title}>IRC-2020-After Movie</h1>
            </div>
            <Box boxShadow={24} className={classes.player}>
                <ReactPlayer url='https://youtu.be/B3iRm-0kSSY' controls='true' volume='1' muted='true' height='100%' width='100%' loop='true' playing />
            </Box>
        </div>
    </div>
    );
}
