import React, { useState, useEffect, useRef } from 'react';

import { Link, animateScroll as scroll } from "react-scroll";
import { makeStyles } from "@material-ui/core/styles";

import Arrowd from "@material-ui/icons/ArrowDropDown";

const useStyles = makeStyles({
    // #00F8C8 #E0E1EE
    container: {
        position: 'relative',
        height: '150vh',
        backgroundColor: '#6C44D2',
        // paddingTop: '5vh'
    },
    title: {
        position: 'absolute',
        fontSize: '10vh',
        marginLeft: '7vw',
        marginTop: '10vh',
        color: '#00F8C8',
    },
    titleDuring: {
        position : 'fixed',
        left : '8.5vw',
        top : '45vh',
        marginLeft : '0vw',
        marginTop : '0vh',
    },
    titleAfter: {
        position : 'absolute',
        left : '0vw',
        top : '0vw',
        marginLeft : '10vw',
        marginTop : '50vh',
    }

});

const title = {
    position: 'absolute',
    fontSize: '10vh',
    marginLeft: '7vw',
    marginTop: '10vh',
    color: '#00F8C8',
};
const titleDuring = {
    position : 'fixed',
    left : '8.5vw',
    top : '45vh',
    marginLeft : '0vw',
    marginTop : '0vh',
};
const titleAfter = {
    position : 'absolute',
    left : '0vw',
    top : '0vw',
    marginLeft : '10vw',
    marginTop : '50vh',
};


function getWindowDimensions() {
    const { innerWidth: width, innerHeight: height } = window;
    return {
        width,
        height
    };
}

export default function SectionSponsorship(props) {
    const classes = useStyles();

    const prevScrollY = useRef(0);
    const [windowDimensions, setWindowDimensions] = useState(getWindowDimensions());

    useEffect(() => {
        const classes = useStyles();
        ////////////////////////////resize event///////////////////////////
        function handleResize() {
            setWindowDimensions(getWindowDimensions());
            console.log(windowDimensions.width);
        }
        window.addEventListener('resize', handleResize);

        ////////////////scroll event//////////////////////////
        const handleScroll = () => {
            const currentScrollY = window.scrollY;
            prevScrollY.current = currentScrollY;
            console.log(currentScrollY);
            let element = document.getElementById('sponsortitle').classsName;
            if (currentScrollY > 8914 && currentScrollY < 9263) {
                element = {titleDuring };
            } else if (currentScrollY >= 9263) {
                element = {titleAfter};
            }
            else {
                element = {title};
            }
        };

        window.addEventListener("scroll", handleScroll, { passive: true });

        return () => window.removeEventListener("scroll", handleScroll);
        return () => window.removeEventListener('resize', handleResize);
    }, [prevScrollY]);


    return (
        <div className={classes.container}>
            <div id="sponsortitle" className={classes.title}>
                <h1>SPONSORS</h1>
            </div>
        </div>
    );
}
