import React, { useState, useCallback } from "react";
import { Link } from "react-router-dom";
// @material-ui/core components
import { makeStyles } from "@material-ui/core/styles";
import Gallery from 'react-photo-gallery';
import Carousel, { Modal, ModalGateway } from "react-images";
import photos from "../GalleryPage/components/photo";
import selectedImage from "../GalleryPage/components/selectedImage";

import Home from "@material-ui/icons/Home";

const useStyles = makeStyles({
  container: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center'
  },
  navBar: {
    position: 'fixed',
    top: '0px',
    left: '0px',
    height: '6vh',
    width: '100vw',
    backgroundColor: 'white',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'space-around'
  },
  mainCard: {
    marginTop: '8vh',
    width: '92vw'
  },
  title: {
    fontSize: '2em',
    color: '#fc4267'
  },
  brand: {
    fontFamily: 'nordi',
    fontSize: '2em',
    color: '#fc4267',
  },
});

export default function GalleryPage(props) {
  const classes = useStyles();

  const [currentImage, setCurrentImage] = useState(0);
  const [viewerIsOpen, setViewerIsOpen] = useState(false);

  const openLightbox = useCallback((event, { photo, index }) => {
    setCurrentImage(index);
    setViewerIsOpen(true);
  }, []);

  const closeLightbox = () => {
    setCurrentImage(0);
    setViewerIsOpen(false);
  };

  const imageRenderer = ({ index, left, top, key, photo }) => (
      <SelectedImage
        key={key}
        margin={"2px"}
        index={index}
        photo={photo}
        left={left}
        top={top}
      />
);



  return (
    <div>
      <div className={classes.navBar}>
        <Link to="/">
          <Home style={{ fontSize: '2em', color: "#fc4267" }} />
        </Link>
        <div className={classes.title}>
          Gallery
        </div>
        <Link to="/">
          <div className={classes.brand}>
            rudra
        </div>
        </Link>
      </div>
      <div className={classes.container}>
        <div className={classes.mainCard}>
          <Gallery photos={photos} renderImage={imageRenderer} onClick={openLightbox} />
          <ModalGateway>
            {viewerIsOpen ? (
              <Modal onClose={closeLightbox}>
                <Carousel
                  currentIndex={currentImage}
                  views={photos.map(x => ({
                    ...x,
                    srcset: x.srcSet,
                    caption: x.title
                  }))}
                />
              </Modal>
            ) : null}
          </ModalGateway>
        </div>
      </div>
    </div>
  );
}
