import React, { useState } from 'react';
import { makeStyles } from "@material-ui/core/styles";
import Home from "assets/icon/home.svg";
import Phone from "assets/icon/phone.png";
import Twitter from "assets/icon/Twitter";
import { SvgIcon } from '@material-ui/core';

const useStyles = makeStyles({
    // #FFAC62
    // #1E6F79
    container: {
        height: '100vh',
        backgroundColor: '#1E6F79'
    },
    titleContainer: {
        display: 'flex',
        paddingTop: '5vh',
        height: '10vh',
        alignItems: 'center',
        justifyContent: 'center',
        color: '#FFAC62',
    },
    address: {
        position: 'absolute',
        marginTop: '20vh',
        marginLeft: '10vw',
        height: '10vh',
        flex: '1',
        flexDirection: 'vertical',
        alignItems: 'center',
        justifyContent: 'center',
    },
    homeIcon: {
        height: '100px',
        color: '#FFAC62',
        padding: '10px',
        fontSize: '80px'
    },
    Icon: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        border: '2px solid #FFAC62',
        borderRadius: '50%',
        height: '15vh',
        width: '7vw',
        margin: '0 0 2vh 3vw',
    },
    homeText: {
        color: '#FFAC62',
        flex: '1',
        alignItems: 'center',
        justifyContent: 'center',
        width: '15vw',
        fontSize: '2vh'
    },
    phone: {
        position: 'absolute',
        marginTop: '20vh',
        marginLeft: '40vw',
        height: '10vh',
        flex: '1',
        flexDirection: 'vertical',
        alignItems: 'center',
        justifyContent: 'center',
    },
    email: {
        position: 'absolute',
        marginTop: '20vh',
        marginLeft: '70vw',
        height: '10vh',
        flex: '1',
        flexDirection: 'vertical',
        alignItems: 'center',
        justifyContent: 'center',
    },
    social: {
        position: 'absolute',
        marginTop: '60vh',
        marginLeft: '10vw',
        height: '20vh',
        width: '10vw',
        backgroundColor: 'tarnsparent',
        border: '2px solid #FFAC62',
        borderRadius: '50%',
        flex: '1',
        flexDirection: 'vertical',
        alignItems: 'center',
        justifyContent: 'center',
    },
});

const twitterStyle = () => {
    return (
        {color: '#FFAC62',
        flex: '1',
        flexDirection: 'vertical',
        alignItems: 'center',
        justifyContent: 'center',
        marginLeft: '10vw',
        }
    );
}

export default function SectionPatreon(props) {
    const classes = useStyles();
    const [twitterColor, setTwitterColor] = useState("#FFAC62");

    return (
        <div className={classes.container}>
            <div className={classes.titleContainer}>
                <h1 style={{ fontWeight: 'bold', fontSize: '12vh', paddingTop: '5vh' }}>YOU CAN CONTACT US AT</h1>
            </div>
            <div className={classes.address}>
                <div className={classes.Icon}>
                    <img className={classes.homeIcon} src={Home} />
                </div>
                <div className={classes.homeText}>
                    C-404/405 - Placement Cell, SRM IST, Kattankulathur, Chennai - 603203
                </div>
            </div>
            <div className={classes.phone}>
                <div className={classes.Icon}>
                    <img className={classes.homeIcon} src={Phone} />
                </div>
                <div className={classes.homeText}>
                    +91 78606 77244,
                    +91 83403 97548
                </div>
            </div>
            <div className={classes.email}>
                <div className={classes.Icon}>
                    <div className={classes.homeIcon} style={{ marginTop: '30px' }}> @ </div>
                </div>
                <div className={classes.homeText}>
                    srmmarsroverteam@gmail.com
                </div>
            </div>
            <div className={classes.social}>
                <Twitter color={twitterColor} height="15vh" width="7vw" style={twitterStyle} />
            </div>
        </div>
    );
}
