import React, { useState, useEffect, useRef } from 'react';
import InstagramEmbed from 'react-instagram-embed';
import { Parallax, Background } from 'react-parallax';
import { Link, animateScroll as scroll } from "react-scroll";
import { makeStyles } from '@material-ui/core/styles';

import Box from '@material-ui/core/Box';
import { shadows } from '@material-ui/system';

import bgImage from 'assets/img/bg8.jpg';
import Arrowd from "@material-ui/icons/ArrowDropDown";

const useStyles = makeStyles({

    container: {
        height: '105vh',
        zIndex: '10',
        display: 'flex',
        flexWrap: 'wrap',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'space-around'
    },
    title: {
        fontFamily: 'gotham',
        fontSize: '6vw',
        textStrokeWidth: '5px',
        textStrokeColor: '#fff',
        textFillColor: 'transparent',
    },
    content: {
        display: 'flex',
        flexDirection: 'row',
        width: '100%',
        alignItems: 'center',
        justifyContent: 'space-around'
    },
    instagram: {
        height: '65vh',
        width: '25vw',
        overflowY: 'scroll',
        paddingRight: '-10vw',
        boxShadow: '0 10px 15px #000',
        transition: '250ms ease-in-out',
        scrollbarWidth: 'none',
        '&::-webkit-scrollbar': {
            width: '0px',
            background: 'transparent'
        },
        '&:hover': {
            transform: 'scale(1.1)',
        }
    },
    facebook: {
        height: '65vh',
        width: '25vw',
        zIndex: '1000',
        backgroundColor: 'white',
        boxShadow: '0 10px 15px #000',
        transition: '250ms ease-in-out',
        '&:hover': {
            transform: 'scale(1.1)',
        }
    },
    blog: {
        width: '25vw',
        height: '65vh',
        backgroundColor: 'white',
        boxShadow: '0 10px 15px #000',
        transition: '250ms ease-in-out',
        '&:hover': {
            transform: 'scale(1.1)',
        }
    },

    downvote: {
        height: '5vh',
        width: '100%',
        zIndex: '10000',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: 'black',
        opacity: '0.4',
        transition: '250ms ease-in-out',
        '&:hover': {
          opacity: '0.7'
        }
    },
    arrow: {
        width: "100px",
        height: "100px",
        transition: '1s',
        color: '#fff',
        opacity: '0.3'
    },

    '@media (max-width: 1024px)': {
        container: {
            height: '80vh',
        },
        title: {

        },
        instagram: {
            height: '38vh',
            width: '32vw'
        },
        facebook: {
            height: '38vh',
            width: '33vw'
        },
        blog: {
            height: '38vh',
            width: '33vw'
        }
    },

    '@media (max-width: 800px)': {
        container: {
            height: '250vh',
            zIndex: '10'
        },
        title: {
            position: 'absolute',
            marginLeft: '8vw',
            marginTop: '12vh',
            fontFamily: 'gotham',
            fontSize: '12vh',
            textStrokeWidth: '7px',
            textStrokeColor: '#fff',
            textFillColor: 'transparent',
        },
        outerInsta: {
            position: 'absolute',
            marginLeft: '5vw',
            marginTop: '25vh',
            width: '80vw',
            overflow: 'hidden',
            borderRadius: '2%',
            boxShadow: '5px 5px 7px 7px #888888',
            transition: '500ms ease-in-out',
            '&:hover': {
                transform: 'scale(1.1)',
            }
        },
        instagram: {
            height: '65vh',
            width: '82vw',
            overflowY: 'scroll',
            paddingRight: '-10vw',
            transition: '500ms ease-in-out',
        },
        facebook: {
            position: 'absolute',
            marginLeft: '5vw',
            marginTop: '95vh',
            width: '80vw',
            zIndex: '1000',
            overflowX: 'hidden'
        },
        facebookback: {
            position: 'absolute',
            backgroundColor: 'white',
            marginLeft: '5vw',
            marginTop: '95vh',
            height: '66vh',
            width: '80vw',
            zIndex: '100',
            borderRadius: '2%',
            boxShadow: '5px 5px 7px 7px #888888',
        },
        blog: {
            position: 'absolute',
            marginLeft: '5vw',
            width: '80vw',
            height: '65vh',
            marginTop: '165vh',
            backgroundColor: 'white',
            borderRadius: '2%',
            boxShadow: '5px 5px 7px 7px #888888',
            transition: '500ms ease-in-out',
            '&:hover': {
                transform: 'scale(1.1)',
            }
        },
        downvote: {
            position: 'absolute',
            marginLeft: '37vw',
            marginTop: '-7vh',
            height: '50px',
            width: '50px',
            zIndex: '10000',
        },
        arrow: {
            width: "50px",
            height: "50px",
            marginLeft: "8px",
            transition: '1s',
            color: '#ffffdd',
        },
    },
});

function getWindowDimensions() {
    const { innerWidth: width, innerHeight: height } = window;
    return {
        width,
        height
    };
}


export default function SectionSocial(props) {
    const classes = useStyles();

    const prevScrollY = useRef(0);
    const [windowDimensions, setWindowDimensions] = useState(getWindowDimensions());

    return (
        <Parallax
            blur={{ min: -100, max: 100 }}
            bgImage={bgImage}
            bgImageAlt="the cat"
            strength={1300}
        >
            <div className={classes.container}>
                <div className={classes.title}>Social</div>
                <div className={classes.content}>
                    <Box className={classes.instagram}>
                        <InstagramEmbed
                            url='https://www.instagram.com/p/BtTMxnYhSlZ/'
                            maxWidth={480}
                            hideCaption={false}
                            containerTagName='div'
                            protocol=''
                            injectScript
                            onLoading={() => { }}
                            onSuccess={() => { }}
                            onAfterRender={() => { }}
                            onFailure={() => { }}
                        />
                    </Box>
                    <Box className={classes.facebook}>
                        <iframe src="https://www.facebook.com/plugins/post.php?href=https%3A%2F%2Fwww.facebook.com%2Fsrmteamrudra%2Fposts%2F2406254082726962%3A0&width=500"
                            style={{ overflow: "hidden", height: '100%', width: '100%'}}
                            scrolling="no"
                            frameborder="0"
                            allowtransparency="false"
                            allow="encrypted-media">
                        </iframe>
                    </Box>
                    <Box className={classes.blog}>
                        <blockquote class="embedly-card"><h4><a href="https://medium.com/@srmmarsroverteam">Team Rudra - Medium</a></h4><p>Read writing from Team Rudra on Medium. RUDRA is an official robotics team of SRM Institute of Science and Technology that specializes in building mars rover prototypes.</p></blockquote>
                    </Box>
                </div>
               <Link className={classes.downvote}
                    to="sponsorship"
                    spy={true}
                    smooth={true}
                    offset={0}
                    duration={1000} >
                    <Arrowd id="pral" className={classes.arrow} />
                </Link>
            </div>
        </Parallax>
    );
}
