import React, { useState, useEffect, useRef } from "react";
import { Link, animateScroll as scroll } from "react-scroll";
import { Parallax, Background } from "react-parallax";
import Button from "components/CustomButtons/Button.js";
import Paper from "@material-ui/core/Paper";
import { makeStyles } from "@material-ui/core/styles";
import Arrowd from "@material-ui/icons/ArrowDropDown";
import wheeleez from "assets/img/image/sponsors/wheeleez.webp";
import nvidia from "assets/img/image/sponsors/Nvidia_logo.webp";
import anet from "assets/img/image/sponsors/anet.webp";
import unfynite from "assets/img/image/sponsors/unfynite.webp";
import incwave from "assets/img/image/sponsors/inception_wave.webp";
import monster from "assets/img/image/sponsors/monster.webp";
import solidworks from "assets/img/image/sponsors/solidworks.webp";
import sparkfun from "assets/img/image/sponsors/sparkfun.webp";
import protocase from "assets/img/image/sponsors/protocase.webp";
import altium from "assets/img/image/sponsors/Altium.webp";
import sukrit from "assets/img/image/sponsors/sukrit.webp";
import omgvh from "assets/img/image/sponsors/omgvh.webp";
import { isMobile } from "react-device-detect";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const useStyles = makeStyles({
  "@media (min-width: 800px)": {
    container: {
      backgroundColor: "#12121C",
      height: "150vh",
      display: "flex",
      flexDirection: "row",
      alignItems: "center",
      justifyContent: "space-around",
    },
    title: {
      fontSize: "5em",
      fontWeight: "600",
      textStrokeWidth: "2px",
      textStrokeColor: "#D9D9D9",
      textFillColor: "transparent",
    },
    sponsorship: {
      height: '40vh',
      width: '60vw',
    },
    sponsor: {
      height: "40vh",
      width: "50vw",
      display: "flex",
      alignItems: "center",
      justifyContent: "space-around",
    },
    image: {
      height: '80%',
      margin: 'auto'
    },
  },
  "@media (max-width: 800px)": {
    container: {
      backgroundColor: "#12121C",
      height: "130vh",
    },
    sponsor: {
      height: "30vh",
      // width: '50%',
      backgroundColor: "#202432",
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
    },
    title: {
      height: "5vh",
      width: "100%",
      display: "flex",
      flexDirection: "row",
      alignItems: "center",
      justifyContent: "space-around",
    },
    h1: {
      fontFamily: "gotham",
      fontSize: "15vw",
      textStrokeWidth: "2px",
      textStrokeColor: "#8896BF",
      textFillColor: "transparent",
    },
    image: {
      width: "98%",
    },
    button: {
      width: "100%",
      margin: "5vh 0 4vh 0",
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
    },
    downvote: {
      marginTop: "5vh",
      height: "5vh",
      width: "100%",
      zIndex: "10000",
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
      backgroundColor: "black",
      opacity: "0.4",
      transition: "250ms ease-in-out",
      "&:hover": {
        opacity: "0.7",
      },
    },
    arrow: {
      width: "100px",
      height: "100px",
      transition: "1s",
      color: "#fff",
      opacity: "0.3",
    },
  },
});

// const params = {
//   effect: "coverflow",
//   grabCursor: true,
//   centeredSlides: true,
//   slidesPerView: "auto",
//   coverflowEffect: {
//     rotate: 50,
//     stretch: 0,
//     depth: 100,
//     modifier: 1,
//     slideShadows: true,
//   },
//   pagination: {
//     el: ".swiper-pagination",
//   },
// };

export default function SectionSponsorship(props) {
  const classes = useStyles();

  const prevScrollY = useRef(0);

  var settings = {
    dots: false,
    infinite: true,
    adaptiveHeight: true,
    speed: 500,
    autoplay: true,
    autoplaySpeed: 3000,
    centerMode: true,
    slidesToShow: 1,
    slidesToScroll: 1,
    fade: true,
  };

  var settingsm = {
    dots: true,
    infinite: true,
    adaptiveWidth: true,
    speed: 500,
    autoplay: true,
    autoplaySpeed: 3000,
    centerMode: true,
    slidesToShow: 2,
    // fade: true,
  };

  if (isMobile) {
    return (
      <div className={classes.container}>
        <br />
        <br />
        <div className={classes.title}>
          <h1 className={classes.h1}>Sponsors</h1>
        </div>
        <br />
        <br />
        <br />
        <Slider {...settings}>
          <Paper elevation={24} className={classes.sponsor}>
            <img
              className={classes.image}
              style={{ marginTop: "8vh" }}
              src={wheeleez}
              width="100%"
            ></img>
          </Paper>
          <Paper elevation={24} className={classes.sponsor}>
            <img
              className={classes.image}
              style={{ margin: "2vh 0 0 1vw" }}
              src={nvidia}
              width="100%"
            ></img>
          </Paper>
          <Paper elevation={24} className={classes.sponsor}>
            <img className={classes.image} src={anet} width="100%"></img>
          </Paper>
        </Slider>
        <br />
        <br />
        <div className={classes.title}>
          <h1 className={classes.h1}>Partners</h1>
        </div>
        <br />
        <br />
        <br />
        <Slider {...settings}>
          <Paper elevation={24} className={classes.sponsor}>
            <img
              className={classes.image}
              src={unfynite}
              style={{ width: "80%", margin: "auto", marginTop: "2vh" }}
              width="100%"
            ></img>
          </Paper>
          <Paper elevation={24} className={classes.sponsor}>
            <img className={classes.image} src={incwave} width="100%"></img>
          </Paper>
          <Paper elevation={24} className={classes.sponsor}>
            <img
              className={classes.image}
              src={monster}
              style={{ marginTop: "7vh" }}
              width="100%"
            ></img>
          </Paper>
        </Slider>
        <div className={classes.button}>
          <Button size="large">Know About Our Sponsors</Button>
        </div>
        <Link
          className={classes.downvote}
          to="patron"
          spy={true}
          smooth={true}
          offset={0}
          duration={1000}
        >
          <Arrowd id="pral" className={classes.arrow} />
        </Link>
      </div>
    );
  }
  return (
    <div className={classes.container}>
      <div className={classes.sponsorship}>
        <Slider {...settingsm}>
          <div className={classes.sponsor}>
            <img className={classes.image} src={wheeleez} />
          </div>
          <div className={classes.sponsor}>
            <img className={classes.image} src={nvidia} />
          </div>
          <div className={classes.sponsor}>
            <img className={classes.image} src={sparkfun} />
          </div>
          {/* <div className={classes.sponsor}>
          <img className={classes.image} src={solidworks} />
        </div> */}
          <div className={classes.sponsor}>
            <img className={classes.image} src={anet} />
          </div>
          {/* <img className={classes.sponsor} src={protocase}/>
                <img className={classes.sponsor} src={altium}/>
                <img className={classes.sponsor} src={sukrit}/> */}
        </Slider>
      </div>
    </div>
  );
}
