import React, { useState, useEffect, useRef } from 'react';

import { Link, animateScroll as scroll } from "react-scroll";
import { makeStyles } from "@material-ui/core/styles";

import Arrowd from "@material-ui/icons/ArrowDropDown";

const useStyles = makeStyles({
    // #00F8C8 #E0E1EE
    container: {
        position: 'relative',
        height: '150vh',
        backgroundColor: '#6C44D2',
        // paddingTop: '5vh'
    },
    title:{
        positon: 'fixed',
        // top: '10vh',
        // left: '10vw',
        // width: '100%'
        // marginLeft: '7vw',
        // marginTop: '10vh',
        color: '#00F8C8',
        fontSize: '100px',
        // zIndex: '100'
    }
});

function getWindowDimensions() {
    const { innerWidth: width, innerHeight: height } = window;
    return {
        width,
        height
    };
}

export default function SectionSponsorship(props) {
    const classes = useStyles();

    const prevScrollY = useRef(0);
    const [windowDimensions, setWindowDimensions] = useState(getWindowDimensions());

    useEffect(() => {
        ////////////////////////////resize event///////////////////////////
        function handleResize() {
            setWindowDimensions(getWindowDimensions());
            console.log(windowDimensions.width);
        }
        window.addEventListener('resize', handleResize);

        ////////////////scroll event//////////////////////////
        const handleScroll = () => {
            const currentScrollY = window.scrollY;
            prevScrollY.current = currentScrollY;
                console.log(currentScrollY);
                // let element = document.getElementById('sponsortitle').style;
                // if(currentScrollY > 8914 && currentScrollY < 9263){
                //     element.position = 'fixed';
                //     element.marginLeft = '10vw';
                //     element.marginTop = '10vw';

                // } else {
                //     element.position = 'absolute';
                // }
        };

        window.addEventListener("scroll", handleScroll, { passive: true });

        return () => window.removeEventListener("scroll", handleScroll);
        return () => window.removeEventListener('resize', handleResize);
    }, [prevScrollY]);


    return(
        <div className={classes.container}>
            <div id="sponsortitle" className={classes.title}>
                SPONSORS
            </div>
        </div>
    );
}
