import React, { useState, useEffect, useRef } from "react";
import { Link, animateScroll as scroll } from "react-scroll";
import { Parallax, Background } from "react-parallax";
import Button from "components/CustomButtons/Button.js";
import Paper from "@material-ui/core/Paper";
import { makeStyles } from "@material-ui/core/styles";
import Arrowd from "@material-ui/icons/ArrowDropDown";
import wheeleez from "assets/img/image/sponsors/wheeleez.webp";
import nvidia from "assets/img/image/sponsors/Nvidia_logo.webp";
import anet from "assets/img/image/sponsors/anet.webp";
import unfynite from "assets/img/image/sponsors/unfynite.webp";
import monster from "assets/img/image/sponsors/monster.webp";
import solidworks from "assets/img/image/sponsors/solidworks.webp";
import sparkfun from "assets/img/image/sponsors/sparkfun.webp";
import protocase from "assets/img/image/sponsors/protocase.webp";
import altium from "assets/img/image/sponsors/Altium.webp";
import omgvh from "assets/img/image/sponsors/omgvh.webp";
import divvela from "assets/img/image/sponsors/divvela.webp";
import ansys from "assets/img/image/sponsors/ansys.webp";
import basicmicro from "assets/img/image/sponsors/basicmicro.webp";
import irobocom from "assets/img/image/sponsors/irobocom.webp";
import semikart from "assets/img/image/sponsors/semikart.webp";
import srmpedia from "assets/img/image/sponsors/srmpedia.webp";
import { isMobile } from "react-device-detect";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const useStyles = makeStyles({
  "@media (min-width: 800px)": {
    container: {
      backgroundImage: "linear-gradient(to bottom, #D9D9D9, #A6A6A6);",
      height: "160vh",
      display: "flex",
      flexDirection: "column",
      alignItems: "center",
      justifyContent: "space-around",
    },
    title: {
      fontSize: "7em",
      fontWeight: "600",
      textStrokeWidth: "2px",
      textStrokeColor: "#D9D9D9",
      textFillColor: "transparent",
      filter: 'drop-shadow(7px 0 7px #000)'
    },
    sponsorship: {
      height: "70vh",
      width: "90vw",
    },
    sponsor: {
      height: "50vh",
      width: "50vw",
      display: "flex",
      alignItems: "center",
      justifyContent: "space-around",
      transition: "500ms ease-in-out",
      transform: "scale(0.9)",
    },
    sponsortrans: {
      height: "40vh",
      width: "50vw",
      display: "flex",
      alignItems: "center",
      justifyContent: "space-around",
      transition: "500ms ease-in-out",
      transform: "scale(1.2)",
    },
    image: {
      maxHeight: "40vh",
      maxWidth: "25vw",
      margin: "auto",
      transform: "translateY(50px)",
      filter: 'drop-shadow(7px 0 7px #000)'
    },
  },
  "@media (max-width: 800px)": {
    container: {
      backgroundColor: "#12121C",
      height: "130vh",
    },
    sponsor: {
      height: "30vh",
      // width: '50%',
      backgroundColor: "#202432",
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
    },
    title: {
      height: "5vh",
      width: "100%",
      display: "flex",
      flexDirection: "row",
      alignItems: "center",
      justifyContent: "space-around",
    },
    h1: {
      fontFamily: "gotham",
      fontSize: "15vw",
      textStrokeWidth: "2px",
      textStrokeColor: "#8896BF",
      textFillColor: "transparent",
    },
    image: {
      width: "98%",
    },
    button: {
      width: "100%",
      margin: "5vh 0 4vh 0",
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
    },
    downvote: {
      marginTop: "5vh",
      height: "5vh",
      width: "100%",
      zIndex: "10000",
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
      backgroundColor: "black",
      opacity: "0.4",
      transition: "250ms ease-in-out",
      "&:hover": {
        opacity: "0.7",
      },
    },
    arrow: {
      width: "100px",
      height: "100px",
      transition: "1s",
      color: "#fff",
      opacity: "0.3",
    },
  },
});

// const params = {
//   effect: "coverflow",
//   grabCursor: true,
//   centeredSlides: true,
//   slidesPerView: "auto",
//   coverflowEffect: {
//     rotate: 50,
//     stretch: 0,
//     depth: 100,
//     modifier: 1,
//     slideShadows: true,
//   },
//   pagination: {
//     el: ".swiper-pagination",
//   },
// };

export default function SectionSponsorship(props) {
  const classes = useStyles();

  const sponsorship = [
    [
    nvidia,
    anet,
    sparkfun,
    wheeleez,
    solidworks,
    ansys,
    protocase,
    altium,
    divvela,
    basicmicro,
    omgvh],
    [
      unfynite,
      irobocom,
      semikart,
      monster,
      srmpedia
    ]
  ];

  const [curridx, setcurridx] = useState([0, 0]);

  var settings = {
    dots: false,
    infinite: true,
    adaptiveHeight: true,
    speed: 500,
    autoplay: true,
    autoplaySpeed: 3000,
    centerMode: true,
    slidesToShow: 1,
    slidesToScroll: 1,
    fade: true,
  };

  var settingsm = {
    infinite: true,
    adaptiveWidth: true,
    speed: 500,
    autoplay: true,
    autoplaySpeed: 1500,
    centerMode: true,
    slidesToShow: 3,
    arrows: false,
  };

  var settingsmsp = {
    ...settingsm,
    beforeChange: (oldidx, newidx) => {
      setcurridx([newidx, curridx[1]]);
    },
  };
  var settingsmpa = {
    ...settingsm,
    beforeChange: (oldidx, newidx) => {
      setcurridx([curridx[0], newidx]);
    },
  };

  if (isMobile) {
    return (
